<!--
  Adapted from https://github.com/abakermi/vue-feeds-youtube/blob/master/src/components/VFeedYoutube.vue
  Adaptations:
    * Use youtube API in stead of cors-anywhere (which is down)
    * Change styling from ID based to class based to allow for multiple feeds to show.
    * Add number of videos that are displayed as parameter.
-->
<template>
  <div>
    <h2 class="title is-2">{{ title }}</h2>
    <div class="content">
      <div id="videogroup" class="videogroup">
        <div :id="'videolist-' + playlistID" class="videolist"></div>
      </div>
    </div>
  </div>
</template>

<script>
import { DateTime, Interval } from "luxon";
const humanizeDuration = require("humanize-duration");

export default {
  props: {
    // The title that shows above the playlist
    title: {
      type: String,
      required: true,
    },
    // The ID of the playlist to view
    playlistID: {
      type: String,
      required: true,
    },
    // The number of videos you want to display
    numberOfVideosToDisplay: {
      type: Number,
      required: true,
    },
  },
  // When component is mounted, add in the google api script
  mounted() {
    let apiScript = document.createElement("script");
    apiScript.setAttribute("src", "https://apis.google.com/js/api.js");
    apiScript.onload = () => {
      console.log("Injected google api script");

      // NOTE: gapi is defined in the google API that dynamically gets injected
      // load the createFeed() method that does all the work.
      // eslint-disable-next-line no-undef
      gapi.load("client", this.createFeed);
    };

    document.head.appendChild(apiScript);
  },
  methods: {
    /**
     * Given playlistData from youtube, builds a playlist view and returns HTML to be injected
     */
    buildPlaylistView(playlistDataItems, amountOfVideos) {
      // Select only the wanted items
      let videosInPlaylist = playlistDataItems.slice(0, amountOfVideos);

      let out = "<ul>";
      for (const item of videosInPlaylist) {
        console.log(item.snippet);
        let id = item.snippet.resourceId.videoId;
        let videoTitle = item.snippet.title;
        // Get text from time values
        let start = DateTime.fromISO(item.snippet.publishedAt);
        let finish = DateTime.now();
        let publishedAt = humanizeDuration(
          Interval.fromDateTimes(start, finish).toDuration().valueOf(),
          {
            round: true,
            largest: 3,
          }
        );
        out += `<li><div class="content"><div class="thumbnail"><iframe class="thumbnail" allowfullscreen="1" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" title="YouTube video player - ${videoTitle}" src="https://www.youtube.com/embed/${id}?enablejsapi=1"></iframe></div><div class="details"><div class="metadata"><a class="entry-title"> ${videoTitle} </a> <span class="date">${publishedAt} ago</span></div></div></div></li>`;
      }
      out += "</ul>";
      return out;
    },
    /**
     * Does the api calls, then builds the playlistview by passing data to buildPlaylistView()
     */
    createFeed() {
      // NOTE: gapi is defined in the google API that dynamically gets injected
      // eslint-disable-next-line no-undef
      gapi.client
        .init({
          apiKey: this._get(),
        })
        .then(() => {
          // NOTE: directly including playlistID for some reason defaults to null.
          const id = this.playlistID;
          // eslint-disable-next-line no-undef
          return gapi.client.request({
            path: `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&playlistId=${id}`,
          });
        })
        .then((response, reason) => {
          // if reason is included, there was an error
          if (reason) {
            console.log(`Error! '${reason.result.error.message}'`);
            return null;
          } else {
            if (response.status == 200) {
              console.log("Retrieved 200 code in createFeed()");
              // Success in getting information
              // Build HTML to inject
              const htmlToInject = this.buildPlaylistView(
                response.result.items,
                this.numberOfVideosToDisplay
              );
              // Inject it
              document.getElementById(
                `videolist-${this.playlistID}`
              ).innerHTML = htmlToInject;
            } else {
              // Non-200 status code, probably error.
              console.log(
                `Non-200 status code! '${JSON.stringify(response, null, 2)}'`
              );
            }
          }
        });
    },
    // Ignore this please. Please?
    _get() {
      return [][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]][([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+([][[]]+[])[+!+[]]+(![]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[])[+!+[]]+([][[]]+[])[+[]]+([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+(!![]+[])[+!+[]]]((!![]+[])[+!+[]]+(!![]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+([][[]]+[])[+[]]+(!![]+[])[+!+[]]+([][[]]+[])[+!+[]]+(+[![]]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+!+[]]]+(!![]+[])[!+[]+!+[]+!+[]]+(+(!+[]+!+[]+!+[]+[+!+[]]))[(!![]+[])[+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+([]+[])[([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+([][[]]+[])[+!+[]]+(![]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[])[+!+[]]+([][[]]+[])[+[]]+([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+(!![]+[])[+!+[]]][([][[]]+[])[+!+[]]+(![]+[])[+!+[]]+((+[])[([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+([][[]]+[])[+!+[]]+(![]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[])[+!+[]]+([][[]]+[])[+[]]+([][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]]+[])[!+[]+!+[]+!+[]]+(!![]+[])[+[]]+(!![]+[][(![]+[])[+[]]+(![]+[])[!+[]+!+[]]+(![]+[])[+!+[]]+(!![]+[])[+[]]])[+!+[]+[+[]]]+(!![]+[])[+!+[]]]+[])[+!+[]+[+!+[]]]+(!![]+[])[!+[]+!+[]+!+[]]]](!+[]+!+